import React from "react";

import { BTN_VARIANT } from "../../../common/components/BurdiButton/constants";
import {
  StyledInput,
  UseButton,
  Wrapper,
  BurdiButtonAction,
  StyledInputBase,
  StyledSelect,
  MaxButton,
  Bloc,
  WrapperSwitch,
  EquippedSwitch,
  NoNearbyInventories
} from "./styles";
import { MenuItem } from "../../../onShop/components/Articles/Article/styles";

const renderNearbyInventory = ({ id, name }) => (
  <MenuItem key={`nearbyInventory-${id}`} value={id}>
    {name}
  </MenuItem>
);

export default ({
  isEquipable,
  onChangeQuantity,
  onUse,
  onEquip,
  onSetMaxQuantity,
  nearbyInventories = [],
  selectedNearbyInventoryId,
  onChangeNearbyInventory,
  quantity = 1,
  onBlurQuantity,
  onDelete,
  onTransfer,
  isEquipped = false,
  isDisabledTransfer,
  isUsable = true,
  isDeletable = true,
  wording
}) => {
  const selectAllInput = event => event.target.select();

  return (
    <Wrapper>
      <Bloc>
        {isUsable && (
          <UseButton
            label={wording.use}
            variant={BTN_VARIANT.secondary}
            color={"#06d2f2"}
            onClick={onUse}
            disabled={!isUsable}
          />
        )}
        {isEquipable && (
          <WrapperSwitch>
            <EquippedSwitch
              size={"small"}
              checked={isEquipped}
              onChange={onEquip}
            />
          </WrapperSwitch>
        )}
      </Bloc>
      <Bloc>
        <StyledInput
          type={"number"}
          onChange={onChangeQuantity}
          value={quantity}
          onBlur={onBlurQuantity}
          onClick={selectAllInput}
        />
        <MaxButton
          color={"#3b4e5e"}
          label={wording.max}
          onClick={onSetMaxQuantity}
        />
        {nearbyInventories.length > 0 ? (
          <StyledSelect
            displayEmpty
            value={selectedNearbyInventoryId}
            onChange={onChangeNearbyInventory}
            input={<StyledInputBase />}
          >
            <MenuItem key={`nearbyInventory-none`} value={"none"}>
              {wording.noneInventory}
            </MenuItem>
            {nearbyInventories.map(renderNearbyInventory)}
          </StyledSelect>
        ) : (
          <NoNearbyInventories>
            {wording.noNearbyInventories}
          </NoNearbyInventories>
        )}
        {nearbyInventories.length > 0 && (
          <BurdiButtonAction
            iconLeftUrl={
              "data:image/svg+xml;base64,"
            }
            color={"#ffd948"}
            iconLeftSize={25}
            disabled={isDisabledTransfer}
            variant={BTN_VARIANT.none}
            onClick={onTransfer}
          />
        )}
        {isDeletable && (
          <BurdiButtonAction
            color={"#f16666"}
            iconLeftSize={25}
            iconLeftUrl={
              "data:image/svg+xml;base64,"
            }
            variant={BTN_VARIANT.none}
            onClick={onDelete}
            disabled={!isDeletable}
          />
        )}
      </Bloc>
    </Wrapper>
  );
};
